import { Component } from "react";
import React from "react";
import Relogio from "./relogio";



class App extends Component {
    constructor(){
        super()
        this.state = {
            mensagem:null
        }
    }
    componentDidMount(){
        fetch('http://numbersapi.com/random/year?json')
        .then(resp => resp.json())
        .then(date => {this.setState({mensagem:date})}).catch(err => console.log("ops... Error" + err));
    }
    
    render(){
        const [mensagem] = this.state

            return(
                <div>
                    <p>{mensagem.text}</p>
                    <Relogio/>
                </div>
            );
    }
}
export default App;
