import { Component } from "react";
import React from "react";



class api extends Component {
    constructor(props){
        super(props)
        this.state = {
            mensagem:''
        };
    }
    componentDidMount(){
        fetch('http://numbersapi.com/random/year?json').then(resp => resp.json()).then(data => {this.setState({mensagem:data})}).catch(err => console.log("ops... Error" + err))
    }
    render(){
        const {mensagem} = this.state;

            return(
                <div>
                    
                    {mensagem.text}

                </div>
            );
    }
}
export default api;
