import React  from  'react';
import { useStopwatch } from 'react-timer-hook';
import {Howl} from 'howler';
import { useState, useEffect} from 'react';

function Relogio() {
  useEffect(()=>{
    const {
      seconds,
      minutes,
      hours,
      days,
      isRunning,
      start,
      pause,
      reset,
    } = useStopwatch({ autoStart: false })
  })
  var beep = new Howl({src:['../public/audio/blip-131856.mp3']});
  const [song ,setSong] = useState(beep)
  
  
    
  
  return (
    <div className='tc'>
      <h1>Cron√¥metro Digital Inteligente</h1>
      <p>Recursos de intervalos de treinos sonoros.</p>
      <div style={{fontSize: '100px'}}>
        <span>{useStopwatch.days}</span>:<span>{useStopwatch.hours}</span>:<span>{useStopwatch.minutes}</span>:<span>{useStopwatch.seconds}</span>
      </div>
      <button onClick={()=> setSong(song.play())}>Som de intervalo</button>
      <p>{useStopwatch.isRunning ? 'Running' : 'Not running'}</p>
      <button onClick={useStopwatch.start}>Start</button>
      <button onClick={useStopwatch.pause}>Pause</button>
      <button onClick={useStopwatch.reset}>Reset</button>
    </div>
  );
}

export default Relogio;
