import React, { Component } from "react";

class apiS extends Component {
    constructor(props){
        super(props)
        this.state = {
            mensagem:null,
    
        }
    }
    componentDidMount(){
        fetch('http://numbersapi.com/random/date')
        .then(resp => resp.json())
        .then(data => {this.setState({mensagem:data})}).catch(err => console.log("ops... Error" + err));
    }
    
    render(){
        const [mensagem] = this.state

        return(
            <div>
                {mensagem}
            </div>
        )
    }
}
export default apiS;
