import React  from  'react';
import { useStopwatch } from 'react-timer-hook';
import {Howl} from 'howler';
import { useRef,useEffect} from 'react';
function Relogio(){
  const{
    seconds,
    minutes,
    hours,
    days,
    isRunning,
    start,
    pause,
    reset,
  } = useStopwatch({ autoStart: false }) 
  
  const beep = useRef(new Howl({src:['blip-131856.mp3'],}))
  const beep3 = useRef(new Howl({src:['blip-131856.mp3','blip-131856.mp3','blip-131856.mp3']}))
  // current é um método do use.Ref() que não precisa de uma nova randeriação e  torna o variável mutável.
  const playBeep = () =>{
    beep.current.play();
  } 
  // problema - verificar a variável minuto chegando a um minuto realizar um sinal sonoro e cheando a 2 min outro sinal sonoro
  // problema menor emitir um sinal quando o tempo parar
  // use efect tornando executal a toda vez que a condição é disparada. 
  useEffect(()=>{
    if(minutes > 0 &&  minutes % 1 === 0){
      return playBeep()
    }
  },[minutes])
  useEffect(()=>{
    if(minutes > 0 &&  minutes % 2 === 0){
      return playBeep()
    }
  },[minutes])
  return (
    <div className='tc'>
      <h1>Cronômetro Digital Inteligente</h1>
      <p>Recursos de intervalos de treinos sonoros.</p>
      <div style={{fontSize: '100px'}}>
        <span>{days}</span>:<span>{hours}</span>:<span>{minutes}</span>:<span>{seconds}</span>
      </div>
      <p>{isRunning ? 'Running' : 'Not running'}</p>
      <button onClick={start}>Start</button>
      <button onClick={pause}>Pause</button>
      <button onClick={reset}>Reset</button>
    </div>
  );
}

export default Relogio;
