import React, { Component } from "react";

class apiS extends Component {
    constructor(){
        super()
        this.state = {
            mensagem:null,
    
        }
    }
    componentDidMount(){
        fetch('http://numbersapi.com/random/date')
        .then(resp => resp.json())
        .then(date => this.setState({mensagem:date})).catch(err => console.log("ops... Error" + err));
    }
    
    render(){
        const [mensagem] = this.state

            return(
                <div>
                    {mensagem}
                </div>
            );
    }
}
export default apiS;
