import { Component } from "react";
import React from "react";
import Relogio from "./relogio";



class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            mensagem:null
        };
    }
    componentDidMount(){
        fetch('http://numbersapi.com/random/date').then(resp => resp.json()).then(data => {this.setState({mensagem:data})}).catch(err => console.log("ops... Error" + err))
    }
    render(){
        const {mensagem} = this.state;

            return(
                <div>
                    
                    <Relogio/>

                    <h1>{mensagem}</h1>
                </div>
            );
    }
}
export default App;
